//Chart.js

import React, {Component} from "react"
import {Bar, Line, Pie} from "react-chartjs-2"
import TodayData from './TodayData';

/* Title function header*/
export default class Chart extends Component{

	constructor(props){
		super(props);
		this.state = {
			id: 0,
			chartData:{
				labels: ["1pm", "2pm", "3pm", "4pm", "5pm", "6", "7", "8", "9", "10", "11", "12"],
				datasets: [
					{
						label:'Wait Times',
						//data:[46.06451613,44.5,47.23333333,48.36666667,52.06451613,50.79310345,49.83333333,46.76666667,42.79310345,56.03333333,46.65517241,42.96153846],
						data: <TodayData id={this.props.id} />,
						borderColor: 'rgba(200, 150, 204, 1)',
						/*'rgba(164, 200, 244, 1)',*/
						backgroundColor: 'rgba(200, 150, 204, 1)'
						// [
						// 'rgba(255, 99, 132, .6)','rgba(255, 99, 132, .6)','rgba(255, 99, 132, .6)',
						// 'rgba(52, 162, 235, .6)','rgba(52, 162, 235, .6)','rgba(52, 162, 235, .6)',
						// 'rgba(255, 206, 86, .6)','rgba(255, 206, 86, .6)','rgba(255, 206, 86, .6)',
						// 'rgba(52, 162, 235, .6)','rgba(52, 162, 235, .6)','rgba(52, 162, 235, .6)',
						// 'rgba(152, 162, 132, .6)','rgba(152, 162, 132, .6)','rgba(152, 162, 132, .6)',
						// 'rgba(222, 200, 235, .6)','rgba(222, 200, 235, .6)','rgba(222, 200, 235, .6)',
						// 'rgba(182, 162, 85, .6)','rgba(182, 162, 85, .6)','rgba(182, 162, 85, .6)',
						// 'rgba(82, 92, 100, .6)','rgba(82, 92, 100, .6)','rgba(82, 92, 100, .6)',
						// 'rgba(52, 190, 62, .6)','rgba(52, 190, 62, .6)','rgba(52, 190, 62, .6)',
						// 'rgba(134, 77, 200, .6)','rgba(134, 77, 200, .6)','rgba(134, 77, 200, .6)',
						// 'rgba(66, 190, 240, .6)','rgba(66, 190, 240, .6)','rgba(66, 190, 240, .6)',
						// 'rgba(240, 132, 58, .6)','rgba(240, 132, 58, .6)','rgba(240, 132, 58, .6)'
						// ]
					}
				]
			}
		}
	}

	async componentDidMount(id) {
		if(id == '0'){
			const response = await fetch('colebergmann.com:5000/callPred/0');
		}
		else if(id == '1'){
			const response = await fetch('colebergmann.com:5000/callPred/1');
		}
		else if(id == '2'){
			const response = await fetch('colebergmann.com:5000/callPred/2');
		}else if(id == '3'){
			const response = await fetch('colebergmann.com:5000/callPred/3');
		}else if(id == '4'){
			const response = await fetch('colebergmann.com:5000/callPred/4');
		}else if(id == '5'){
			const response = await fetch('colebergmann.com:5000/callPred/5');
		}else if(id == '6'){
			const response = await fetch('colebergmann.com:5000/callPred/6');
		}else if(id == '7'){
			const response = await fetch('colebergmann.com:5000/callPred/7');
		}else if(id == '8'){
			const response = await fetch('colebergmann.com:5000/callPred/8');
		}else if(id == '9'){
			const response = await fetch('colebergmann.com:5000/callPred/9');
		}

	    const data = await response.json(); 
	    this.setState({waitTimes: data});

  	}

	render(){
		return(
			<div className = "chart">
				<Line
					data={this.state.chartData}
					options={{
						title: {
							display:true,
							text:"Disneyland Wait Time",
							fontSize:25,
							fontColor: 'black'
						},
						legend:{
							display: true,
							position: 'right'
						},
						scales: {
            				yAxes: [{
                				ticks: {
                    				beginAtZero: true,
                    				fontColor: 'black'
				                }
				            }],
				            xAxes: [{
				            	ticks:{
				            		fontColor: 'black'
				            	}
				            }],

				        }
					}}
				/>
			</div>
		)
	}
}
