/*SpaceMountain.js*/

import React, {Component} from "react"
import {Bar, Line, Pie} from "react-chartjs-2"
import { daytimeData, dayLabels } from "./TodayData";

/* Title function header*/



class SpaceMountain extends Component{

	constructor(props){
		fetch("http://colebergmann.com:5000/callPred/0")
	      .then(res => res.json())
	      .then(
	        (result) => {
	          this.setState({
	            isLoaded: true,
	            items: result
	          });
	        },
	        // Note: it's important to handle errors here
	        // instead of a catch() block so that we don't swallow
	        // exceptions from actual bugs in components.
	        (error) => {
	          this.setState({
	            isLoaded: true,
	            error
	          });
	        }
	      )
		super(props);
		alert(res);
		this.state = {
			error: null,
	      	isLoaded: false,
	      	items: [],
			chartData:{
				labels: ["1pm", "2pm", "3pm", "4pm", "5pm", "6", "7", "8", "9", "10", "11", "12"],
				datasets: [
					{
						label:'Wait Times',
						data:items,
						borderColor: 'rgba(20, 206, 206, 1)',
						backgroundColor: 'rgba(20, 206, 206, 1)'
					}
				]
			}
		}
	}


	render(){
			const { error, isLoaded, items } = this.state;
	    	if (error) {
		    	return <div>Error: {error.message}</div>;
		    }
	    	else if (!isLoaded) {
	      		return <div>Loading...</div>;
	    	} else {
	    		//this.state.chartData["datasets"]["data"] = items;

	      		return (
			<div className = "chart">
				<Line
					data={this.state.chartData}
					options={{
						title: {
							display:true,
							text:"Disneyland Wait Time",
							fontSize:25,
							fontColor: 'black'
						},
						legend:{
							display: true,
							position: 'right'
						},
						scales: {
            				yAxes: [{
                				ticks: {
                    				beginAtZero: true,
                    				fontColor: 'black'
				                }
				            }],
				            xAxes: [{
				            	ticks:{
				            		fontColor: 'black'
				            	}
				            }],

				        }
					}}
				/>
			</div>
		);
	}
}
}


export default SpaceMountain
