import React, {Component} from "react"
import {Bar, Line, Pie} from "react-chartjs-2"
import { daytimeData, dayLabels } from "./TodayData";

/* Title function header*/
class SpaceMountain extends Component{

	constructor(props){
		super(props);
		this.state = {
			chartData:{
				labels: ["1pm", "2pm", "3pm", "4pm", "5pm", "6", "7", "8", "9", "10", "11", "12"],
				datasets: [
					{
						label:'Wait Times',
						data:data,
						borderColor: 'rgba(20, 206, 206, 1)',
						backgroundColor: 'rgba(20, 206, 206, 1)'
					}
				]
			}
		}
	}

	async componentDidMount(){
		const response = await fetch('http://colebergmann.com:5000/callPred/0');
		const data = await response.json(); 

	}

	render(){
		return(
			<div className = "chart">
				<Line
					data={this.state.chartData}
					options={{
						title: {
							display:true,
							text:"Disneyland Wait Time",
							fontSize:25,
							fontColor: 'black'
						},
						legend:{
							display: true,
							position: 'right'
						},
						scales: {
            				yAxes: [{
                				ticks: {
                    				beginAtZero: true,
                    				fontColor: 'black'
				                }
				            }],
				            xAxes: [{
				            	ticks:{
				            		fontColor: 'black'
				            	}
				            }],

				        }
					}}
				/>
			</div>
		)
	}
}


export default SpaceMountain
