/*WaitTime.js*/
//import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';
import React, {Component} from "react"
import {Line} from "react-chartjs-2"
import 'bootstrap/dist/css/bootstrap.css';
import * as moment from 'moment';
import 'moment/locale/pt-br';

class WaitTime extends Component{

	constructor(props){
		fetch("http://colebergmann.com:5000/callPred/0")
	      .then(res => res.json())
	      .then(
	        (result) => {
	          this.setState({
				value: "0",
				name: "Star Tours – The Adventures Continue",
	            isLoaded: true,
	            items: result,
	            chartData:{
	            	labels:
	            	[90],
	            	datasets:[
	            		{
	            			data: result,
							backgroundColor: 'rgba(83, 158, 205, .75)',
	      					borderColor: 'rgba(83, 158, 205, 1)',
						}
	            	],
	            },
	            chartOptions:{
	            		annotation: {
					        annotations: [
					          {
					            type: 'line',
					            mode: "vertical",
					            scaleID: "x-axis-0",
					            value: moment(),
					            borderColor: "red",
					            label: {
					              content: "Current Time",
					              enabled: true,
					              position: "top"
					            }
					          }
					        ]
					      },
						title: {
							text: "Wait Times ",
							display:true,
							fontSize:25,
							fontColor: 'black'
						},
						legend:{
							display: false,
							position: 'right'
						},
						scales: {
            				yAxes: [{
            					scaleLabel:{
            						display: true,
            						labelString: "minutes",
            						fontColor: 'black'
            					},         						
                				ticks: {
                    				beginAtZero: true,
                    				fontColor: 'black'
				                }
				            }],
				            xAxes: [{
			                    type: 'time',
			                    time: {
			                        format: "HH:mm",
			                        unit: 'hour',
			                        unitStepSize: 1,
			                        displayFormats: {
			                            'minute': 'HH:mm',
			                            'hour': 'HH:mm'
			                        },
			                        min: '8:00',
			                        max: '22:40'
			                    },
				            	ticks:{
				            		fontColor: 'black',
				            	}
				            }],

				        }
				}
	          });
	        },
	        // Note: it's important to handle errors here
	        // instead of a catch() block so that we don't swallow
	        // exceptions from actual bugs in components.
	        (error) => {
	          this.setState({
	            isLoaded: true,
	            error
	          });
	        }
	      )
		super(props);
		this.state = {
			value: "0",
			name: "Star Tours – The Adventures Continue",
			error: null,
	      	isLoaded: false,
	      	items: [],
			chartData:{
			labels: [],
				datasets: [
					{
						backgroundColor: 'rgba(83, 158, 205, .75)',
      					borderColor: 'rgba(83, 158, 205, 1)',
					}
				]
			}
		}
		this.handleChange = this.handleChange.bind(this);
    	this.handleSubmit = this.handleSubmit.bind(this);
	}

  	handleChange(event) {
    	this.setState({value: event.target.value});
  	}

	handleSubmit(event) {
		event.preventDefault();
    	fetch("http://colebergmann.com:5000/callPred/"+this.state.value)
	      .then(res => res.json())
	      .then(
	        (result) => {
	          this.setState({
	            isLoaded: true,
	            items: result,
	            chartData:{
	            	labels:
	            	[90],
	            	datasets:[
	            		{
	            			data: result,
							backgroundColor: 'rgba(83, 158, 205, .75)',
	      					borderColor: 'rgba(83, 158, 205, 1)',

	            		}
	            	]

	            },
	            chartOptions:{
						annotation: {
					        annotations: [
					          {
					            type: "line",
					            mode: "vertical",
					            scaleID: "x-axis-0",
					            value: moment(),
					            borderColor: "red",
					            label: {
					              content: "TODAY",
					              enabled: true,
					              position: "top"
					            }
					          }
					        ]
					      },

						title: {
							text: "Wait Times ",
							display:true,
							fontSize:25,
							fontColor: 'black'
						},
						legend:{
							display: false,
							position: 'right'
						},
						scales: {
            				yAxes: [{
            					scaleLabel:{
            						display: true,
            						labelString: "minutes",
            						fontColor: 'black'
            					},         						
                				ticks: {
                    				beginAtZero: true,
                    				fontColor: 'black'
				                }
				            }],
				            xAxes: [{
			                    type: 'time',
			                    time: {
			                        format: "HH:mm",
			                        unit: 'hour',
			                        unitStepSize: 1,
			                        displayFormats: {
			                            'minute': 'HH:mm',
			                            'hour': 'HH:mm'
			                        },
			                        min: '8:00',
			                        max: '22:40'
			                    },
				            	ticks:{
				            		fontColor: 'black',
				            	}
				            }],

				        }
				}
	          });
	        },
	        (error) => {
	          this.setState({
	            isLoaded: true,
	            error
	          });
	        }
	      )
  	}


	render(){
			const { error, isLoaded} = this.state;
	    	if (error) {
		    	return <div>Error: {error.message}</div>;
		    }
	    	else if (!isLoaded) {
	      		return <div>Loading...</div>;
	    	} else {
	      		return (
	      			<div>
	      				<form onSubmit={this.handleSubmit}>
			        	<label>
					          <select value={this.state.value} onChange={this.handleChange}>
					            <option value="0">Star Tours – The Adventures Continue</option>
					            <option value="1">it’s a small world</option>
					            <option value="2">Pirates of the Caribbean</option>
					            <option value="3">Big Thunder Mountain Railroad</option>
					            <option value="4">Indiana Jones™ Adventure</option>
					            <option value="5">Matterhorn Bobsleds</option>
					            <option value="6">Space Mountain</option>
					            <option value="7">Haunted Mansion</option>
					            <option value="8">Splash Mountain</option>
			          		</select>
			        	</label>
			        <input type="submit" value="Submit" />
			      </form>

			<div className = "chart">
				<Line
					data={this.state.chartData}
					options={this.state.chartOptions}
				/>
			</div>
			</div>
		);
	}
}
}


export default WaitTime
