//Chart.js

import React, {Component} from "react"
import {Line} from "react-chartjs-2"
import { popData, yearLabels } from "./DataGraph";

/* Title function header*/
class PopulationChart extends Component{

	constructor(props){
		super(props);
		this.state = {
			chartData:{
				labels: yearLabels,
				datasets: [
					{
						label:'Park Population',
						//data:[46.06451613,44.5,47.23333333,48.36666667,52.06451613,50.79310345,49.83333333,46.76666667,42.79310345,56.03333333,46.65517241,42.96153846],
						data:popData,
						lineTension: 0.1,
      						backgroundColor: 'rgba(75,192,192,0.4)',
      						borderColor: 'rgba(75,192,192,1)',
      						borderCapStyle: 'butt',
      						borderDash: [],
      						borderDashOffset: 0.0,
      						//borderJoinStyle: 'miter',
      						pointBorderColor: 'rgba(75,192,192,1)',
      						pointBackgroundColor: '#fff',
      						pointBorderWidth: 1,
      						pointHoverRadius: 5,
      						pointHoverBackgroundColor: 'rgba(75,192,192,1)',
      						pointHoverBorderColor: 'rgba(220,220,220,1)',
     						pointHoverBorderWidth: 2,
      						pointRadius: 1,
      						pointHitRadius: 10
					}
				]
			}
		}
	}

	render(){
		return(
			<div className = "chart">
				<Line
					data={this.state.chartData}
					options={{
						title: {
							display:true,
							text:"Disneyland Park Population",
							fontSize:25,
							fontColor: 'black'
						},
						legend:{
							display: true,
							position: 'right'
						},
						scales: {
            				yAxes: [{
            					scaleLabel: {
            						display: true,
            						labelString: 'Number of Visitors'
            					},
                				ticks: {
                    				beginAtZero: true
				                }
				            }],
				            xAxes: [{
				            	scaleLabel: {
				            		display:true,
				            		labelString: '2018'
				            	}
				            }]

				        }
					}
				}
				/>
			</div>
		)
	}
}


export default PopulationChart
