//Ride form with the beginning 5 rides as options to choose from 

import React, {Component} from "react"
import Chart from './Chart';

class RideForm extends Component {
  constructor(props) {
    super(props);
    //beginning option (can be anything)
    this.state = {id: 0};
    <Chart id={this.props.id} />
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  async componentDidMount(id) {
    if(id == '0'){
      const response = await fetch('colebergmann.com:5000/callPred/0');
    }
    else if(id == '1'){
      const response = await fetch('colebergmann.com:5000/callPred/1');
    }
    else if(id == '2'){
      const response = await fetch('colebergmann.com:5000/callPred/2');
    }else if(id == '3'){
      const response = await fetch('colebergmann.com:5000/callPred/3');
    }else if(id == '4'){
      const response = await fetch('colebergmann.com:5000/callPred/4');
    }else if(id == '5'){
      const response = await fetch('colebergmann.com:5000/callPred/5');
    }else if(id == '6'){
      const response = await fetch('colebergmann.com:5000/callPred/6');
    }else if(id == '7'){
      const response = await fetch('colebergmann.com:5000/callPred/7');
    }else if(id == '8'){
      const response = await fetch('colebergmann.com:5000/callPred/8');
    }else if(id == '9'){
      const response = await fetch('colebergmann.com:5000/callPred/9');
    }

      const data = await response.json(); 
      this.setState({waitTimes: data});

    }

  handleChange(event) {
    this.setState({id: event.target.id});
    <Chart id={event.target.id} />
  }

//simply a pop up window to check the result
  handleSubmit(event) {
    alert('Your ride is: ' + this.state.value);
    componentDidMount(this.state.value)
    event.preventDefault();
  }

//set up of the drop down 
  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <label>
          <h2 class="paragraph" > Pick your ride: </h2>
          <select id = {this.state.id} onChange={this.handleChange}>
            <option value="Space Mountain">0</option>
            <option value="Pirates of the Caribbean">1</option>
            <option value="Big Thunder Mountain Railroad">2</option>
            <option value="Haunted Mansion">3</option>
            <option value="Splash Mountain">4</option>
            <option value="Matterhorn Bobsleds">5</option>
            <option value="Indiana Jones™ Adventure">6</option>
            <option value="it’s a small world">7</option>
            <option value="Star Tours – The Adventures Continue">8</option>
            <option value="Jungle Cruise">9</option>
          </select>
        </label>
        <input type="submit" value="Submit" />
      </form>
    );
  }
}

export default RideForm
